ns9:component-version-script.script:
#!/bin/bash
## FUNCTION TO CHECK ERROR AND EITHER LOG ADVANCEMENT OR LOG ERROR AND EXIT function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; else echo "$2"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
PROGNAME=`basename $0`
cd /tmp/
if [ "$download_mode" == "CIFS" ] then ###Avoids smbget to always exit with an error code if [ ! -d /root/.smb ] then mkdir /root/.smb fi if [ ! -e /root/.smb/smb.conf ] then touch /root/.smb/smb.conf fi if [ ! -e /root/.smb/smb.conf.append ] then touch /root/.smb/smb.conf.append fi
smbget smb://$REMOTE_USER:$REMOTE_PWD@$REMOTE_SHARE/$MySql_FILE check_error "ERROR - Cannot download sql file $REMOTE_SHARE/$MySql_FILE" "sql file $REMOTE_SHARE/$MySql_FILE downloaded succesfully" elif [ "$download_mode" == "NFS" ] then mount $REMOTE_SHARE /mnt check_error "ERROR - unable to mount nfs share $REMOTE_SHARE" "Successfully mounted remote nfs share $REMOTE_SHARE" cp /mnt/$MySql_FILE /tmp/ check_error "ERROR - Cannot download SQL file $MySql_FILE" "SQL file $MySql_FILE downloaded succesfully" chown $jboss_user: /tmp/$MySql_FILE umount /mnt else error_exit "Copy method not implemented" fi
/usr/bin/mysql -u $MySql_USER -p$MySql_PWD < /tmp/$MySql_FILE check_error "ERROR - Cannot inject sql file $MySql_FILE" "sql file $MySql_FILE injected succesfully"
#Create access from all machines to DB /usr/bin/mysql -u $MySql_USER -p$MySql_PWD mysql -e "insert into user values (\"%\", \"$MySql_USER\", password(\"$MySql_PWD\"), \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"Y\", \"\", \"\", \"\", \"\", 0, 0, 0, 0)" check_error "ERROR - Cannot create user access" "User access created succesfully" /usr/bin/mysql -u $MySql_USER -p$MySql_PWD mysql -e 'flush privileges' check_error "ERROR - Cannot flush privileges" "Privileges flushed succesfully"

